"use strict";

const bootstrap = window.bootstrap;

function planning() {
  const activities = [
    {
      "id": 2,
      "nom": "Activitée DE MES MORT",
      "description": "Ceci est l'activitée 100000",
      "date_debut": "2024-04-21 06:18:01",
      "date_fin": "2024-04-21 07:29:01",
      "adresse": "ff",
      "nb_place": 8,
      "superviser_par": 4,
      "created_at": "2024-04-21T00:18:01.000000Z",
      "updated_at": "2024-04-22T09:46:42.000000Z",
      "supervisor": {
        "id": 4,
        "nom": "EnzO",
        "prenom": "",
        "code_postal": 0,
        "ville": "Yerres",
        "adresse": "",
        "num_telephone": 0,
        "email": "cocodoudo1@gmail.com",
        "role": "benevole",
        "email_verified_at": null,
        "created_at": "2024-02-12T21:35:57.000000Z",
        "updated_at": "2024-04-18T09:42:07.000000Z"
      }
    },
    {
      "id": 3,
      "nom": "Activitée DE MA VIE",
      "description": "Ceci est l'activitée numéro 1",
      "date_debut": "2024-04-21 00:18:07",
      "date_fin": "2024-04-21 00:18:07",
      "adresse": "fff",
      "nb_place": 10,
      "superviser_par": 4,
      "created_at": "2024-04-21T00:18:07.000000Z",
      "updated_at": "2024-04-22T10:45:22.000000Z",
      "supervisor": {
        "id": 4,
        "nom": "EnzO",
        "prenom": "",
        "code_postal": 0,
        "ville": "Yerres",
        "adresse": "",
        "num_telephone": 0,
        "email": "cocodoudo1@gmail.com",
        "role": "benevole",
        "email_verified_at": null,
        "created_at": "2024-02-12T21:35:57.000000Z",
        "updated_at": "2024-04-18T09:42:07.000000Z"
      }
    }
  ];

  const events = activities.map(activity => ({
    title: activity.nom,
    start: activity.date_debut,
    end: activity.date_fin
  }));

  return (
      <div>
        <FullCalendarReact
            plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
            initialView="timeGridWeek"
            headerToolbar={{
              start: 'today prev,next',
              center: 'title',
              end: 'dayGridDay,dayGridWeek,dayGridMonth'
            }}
            height='90vh'
            events={events}
            eventDidMount={info => {
              return new bootstrap.Popover(info.el, {
                title: info.event.title,
                placement: "auto",
                trigger: "hover",
                customClass: "popoverStyle",
                content: "<p>enzo<strong> palpermi</strong>.</p>",
                html: true
              });
            }}
        />
      </div>
  );
}

// Expose the planning function globally
window.planning = planning;
